import React, { useContext, useEffect, useState } from 'react';
import { ShopContaxt } from './shopcontaxt';
import Title from './Title';
import Shopbyproducts from './shopbyproducts';

const Bestseller = () => {
  const [shopItems, setShopItems] = useState([]);
  const { products } = useContext(ShopContaxt);

  useEffect(() => {
    if (products && products.length > 0) {
      setShopItems(products.slice(4, 40));
    }
  }, [products]);

  return (
    <div className="my-10 flex-col">
      {/* âœ… Heading */}
      <div className="text-center max-sm:text-xl py-8 text-3xl">
        <Title text1={"Best"} text2={" Sellers"} />
        <p className="w-3/4 m-auto text-xm max-sm:text-md md:text-base text-gray-600">
          "Discover our best-selling products, carefully selected by our
          customers and trusted for their quality, style, and value."
        </p>
      </div>


      <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-6 px-4">
        {shopItems.length > 0 ? (
          shopItems.map((item) => (
            <Shopbyproducts
              key={item._id}
              id={item._id}
              title={item._title}
              image={item._image}
              name={item._name}
              Subtitle={item._Subtitle}
              price={item._price}
            />
          ))
        ) : (
          <p className="text-center col-span-full">No products available</p>
        )}
      </div>
    </div>
  );
};

export default Bestseller;
