import React, { useContext, useEffect, useState } from 'react'
import { ShopContaxt } from './shopcontaxt'
import Title from './Title';
import Productitem from './Productitem';
// import index from 'toastify';
const ShopBy = () => {
    const {products} = useContext (ShopContaxt);
  const [ShopBy, setShopBy] =useState([]);    
    useEffect(()=>{
      setShopBy(products.slice(0));
    },[])    
  return (
    <div className='my-10'>
        <div className='text-center py-8 text-3xl'>
        <Title text1={'Shop By'} text2={' Category'}/>
        <p className='w-3/4 m-auto text-xm sm:text-sm md:text-base text-gray-600 '></p>
        </div>
        {/* rendering products */}
        <div className='grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4 gap-y-6 '>
          {
            ShopBy.map((item,index)=>(
              <Productitem key={index} id={item._id} image={item._image} name={item._name} price={item._price}/>
            ))
          }
        </div>
    </div>
  )
}

export default ShopBy
// {their usecontaxt hook not api use contaxt hook that helps and also use to access the value of contaxt api}
