import React, { useContext, useEffect, useState } from "react";
import { ShopContaxt } from "./shopcontaxt";
import Title from "./Title";
import Productitem from "./Productitem";

const Bestseller = () => {
  const { products } = useContext(ShopContaxt); // ✅ use products from context only
  const [bestseller, setBestSeller] = useState([]);

  useEffect(() => {
    if (products && products.length > 0) {
      const bestProduct = products.filter((item) => item.bestseller);
      setBestSeller(bestProduct.slice(0, 5));
    }
  }, [products]); // ✅ depend on products

  return (
    <div className="m-10">
      <div className="text-center text-3xl py-8">
        <Title text1={"Best"} text2={"Sellers"} />
        <p className="w-3/4 m-auto text-xs sm:text-sm md:text-base text-gray-600">
          "Discover our best-selling products, carefully selected by our
          customers and trusted for their quality, style, and value."
        </p>
      </div>

      {/* Products grid */}
      <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4 gap-y-6">
        {bestseller.length > 0 ? (
          bestseller.map((item) => (
            <Productitem
              key={item._id}
              id={item._id}
              name={item._name}
              image={item._image}
              price={item._price}
            />
          ))
        ) : (
          <p className="col-span-full text-center">No bestseller products available</p>
        )}
      </div>
    </div>
  );
};

export default Bestseller;
