import React, { useContext, useEffect, useState } from 'react';
import { ShopContaxt } from './shopcontaxt';
import Title from './Title';
const Bestseller = () => {
    const {Products} = useContext (ShopContaxt)
    const [bestseller,setbestseller] =useState([]);
    useEffect (()=>{
        const bestProduct = Products.filter((item)=>(item.bestseller));
        setbestseller(bestProduct.slice(0,5))
    },[])
  return (
    <div className='my-10'>
      <div className='text-center text-3xl py-8'>
        <Title text1={'Best'} text2={'Sellers'}/>
        <p className='w-3/4 m-auto text-xs sm:text-sm md:text-base text-gray-600'>"Discover our best-selling products, carefully selected by our customers and trusted for their quality, style, and value."</p>
      </div>
    </div>
  );
}

export default Bestseller;
