import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const Shopbyproducts = ({ id, Subtitle, title, image, name }) => {
  const [isSquare, setIsSquare] = useState(false);

  useEffect(() => {
    if (image && image[0]) {
      const img = new Image();
      img.src = image[0];
      img.onload = () => {
        setIsSquare(img.width === img.height); // ✅ detect square images
      };
    }
  }, [image]);

  return (
    <Link
      className="block text-gray-700 cursor-pointer hover:shadow-xl transition-transform duration-300 rounded-lg overflow-hidden"
      to={`/product?id=${id}`}
    >
      <div className="p-2">
        {/* ✅ Title */}
        {title && (
          <p className="pt-3 pb-1 text-lg text-center font-semibold">{title}</p>
        )}

        {/* ✅ Image Wrapper */}
        <div
          className={`relative w-full ${
            isSquare ? "" : "aspect-square"
          }`} // only enforce square if not square already
        >
          <img
            className={`${
              isSquare
                ? "w-full h-auto object-contain" // leave natural size if square
                : "absolute inset-0 w-full h-full object-cover"
            }`}
            src={image && image[0] ? image[0] : "/assets/placeholder.png"}
            alt={name || "Product"}
          />
        </div>
      </div>

      {/* ✅ Product Info */}
      <p className="pt-3 pb-1 text-base font-bold text-center">{name}</p>
      {Subtitle && (
        <p className="text-sm text-center font-medium text-gray-600">
          {Subtitle}
        </p>
      )}
    </Link>
  );
};

export default Shopbyproducts;
