import React, { useContext } from 'react';
import { ShopContaxt } from './shopcontaxt';
import { Link } from 'react-router-dom';
import ShopBy from './ShopBy';
const Shopbyproducts = ({id,image,name,price}) => {
    const {currency} = useContext (ShopContaxt)
  return (

     <Link className='text-gary-700 cursor-pointer hover:shadow-lg hover:transition-shadow hover:scale-105 hover:duration-300 hover:ease-in-out  ' to={`/Product/${id}`}>
     <div className='overflow-hidden  '>
        <img src={image[0]} alt="" />
     </div>
     <p className='pt-3 pb-1 text-sm'>{name}</p>
     <p className='text-sm font-medium'>{currency}{price}</p>
     </Link>
  );
}

export default Shopbyproducts;
