import React, { useContext } from 'react';
import { ShopContaxt } from './shopcontaxt';
import { Link } from 'react-router-dom';

const Productitem = ({ id, image, name, price }) => {
  const { currency } = useContext(ShopContaxt);

  // ✅ Fallback image if missing
  const displayImage = Array.isArray(image) && image.length > 0 
    ? image[0] 
    : "/placeholder.png";  // <-- put a placeholder in /public

  return (
    <Link
      className="text-gray-700 cursor-pointer hover:shadow-lg hover:transition-shadow hover:scale-90 hover:ease-in-out"
      to={`/Product/${id}`}
    >
      <div className="overflow-hidden flex items-center justify-center h-40 bg-gray-100">
        <img 
          className="hover:scale-110 transition ease-in-out max-h-40 object-contain" 
          src={displayImage} 
          alt={name || "Product"} 
        />
      </div>
      <p className="pt-3 pb-1 text-sm">{name || "Unnamed Product"}</p>
      <p className="text-sm font-medium">{currency}{price || "N/A"}</p>
    </Link>
  );
};

export default Productitem;
