import React, { useContext } from "react";
import { ShopContaxt } from "./shopcontaxt";
import { Link } from "react-router-dom";

const Productitem = ({ id, image, name, price }) => {
  const { currency } = useContext(ShopContaxt);

  // ensure image is always an array
  const productImage = Array.isArray(image) && image.length > 0 ? image[0] : null;

  return (
    <Link
      className="text-gray-700 cursor-pointer hover:shadow-lg transition-transform hover:scale-105"
      to={`/Product/${id}`}
    >
      <div className="w-full h-60 flex items-center justify-center bg-gray-100 rounded-lg overflow-hidden">
        {productImage ? (
          <img
            src={productImage}
            alt={name}
            className="object-contain w-full h-full"
          />
        ) : (
          <p className="text-gray-400">No Image</p>
        )}
      </div>
      <p className="pt-3 pb-1 text-sm text-center">{name || "Unnamed"}</p>
      <p className="text-sm font-medium text-center">
        {currency}
        {price || "N/A"}
      </p>
    </Link>
  );
};

export default Productitem;
