import React, { useContext, useEffect, useState } from 'react';
import { ShopContaxt } from './shopcontaxt';
import Title from './Title';
import Shopbyproducts from './shopbyproducts';
const ShopBy = () => {
    const [shopItems, setShopItems] = useState([]);
  const { products, currency } = useContext(ShopContaxt);

  useEffect(() => {
    if (products && products.length > 0) {
      setShopItems(products.slice(0, 4));
    }
  }, [products]); 

  return (
    <div className='my-10 flex-col'>
      <div className='text-center max-sm:text-lg py-8 text-3xl'>
        <Title text1={'Shop By'} text2={' Category'} />
        <p className='w-3/4 m-auto text-xm max-sm:text-sm md:text-base text-gray-600'>Discover our wide range of categories including clothing, watches, shoes, and more. 
    Find your perfect style today!</p>
      </div>

      {/* rendering products */}
      <div className='grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4 gap-y-6 ml-[20%] max-md:m-10 max-lg:m-10 max-sm:m-10  '>
        {shopItems.length > 0 ? ( 
          shopItems.map((item) => (
            <Shopbyproducts 
              key={item._id}
              id={item._id}
              title={item._title}
              image={item._image}
              name={item._name}
              Subtitle={item._Subtitle}
              price={item._price}
            />
          ))
        ) : (
          <p className='text-center col-span-full'>No products available</p>
        )}
      </div>
    </div>
  );
};

export default ShopBy;
