import React, { useContext, useEffect, useState } from 'react';
import { ShopContaxt } from './shopcontaxt';
import Title from './Title';
import Productitem from './Productitem';

const ShopBy = () => {
  // const { products } = useContext(ShopContaxt);
  const [shopItems, setShopItems] = useState([]);
  const { products, currency } = useContext(ShopContaxt);

  useEffect(() => {
    if (products && products.length > 0) {
      setShopItems(products.slice(0, 4));
    }
  }, [products]); 

  return (
    <div className='my-10 flex-col '>
      <div className='text-center py-8 text-3xl'>
        <Title text1={'Shop By'} text2={' Category'} />
        <p className='w-3/4 m-auto text-xm sm:text-sm md:text-base text-gray-600'></p>
      </div>

      {/* rendering products */}
      <div className='grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4 gap-y-6 w-full  '>
        {shopItems.length > 0 ? (
          shopItems.map((item) => (
            <Productitem
              key={item._id}
              id={item._id}
              image={item._image}
              name={item._name}
              price={item._price}
            />
          ))
        ) : (
          <p className='text-center col-span-full'>No products available</p>
        )}
      </div>
    </div>
  );
};

export default ShopBy;
