import React, { useContext } from 'react';
import { ShopContaxt } from './shopcontaxt';
import { Link } from 'react-router-dom';
const Productitem = ({id,image,name,price}) => {
    const {currency} = useContext (ShopContaxt)
  return (

     <Link className='text-gary-700 cursor-pointer hover:scale-95 hover:transition hover:ease-in-out hover:shadow-xl hover:shadow-black  ' to={`/Product/${id}`}>
     <div className='overflow-hidden  '>
        <img className='hover:scale-110 transition ease-in-out' src={image[0]} alt="" />
     </div>
     <p className='pt-3 pb-1 text-sm'>{name}</p>
     <p className='text-sm font-medium'>{currency}{price}</p>
     </Link>
  );
}

export default Productitem;
