import React, { useContext } from 'react';
import { ShopContaxt } from './shopcontaxt';
import { Link } from 'react-router-dom';

const Productitem = ({ id, image, name, price }) => {
  const { currency } = useContext(ShopContaxt);

  return (
    <Link 
      className="text-gray-700 cursor-pointer hover:shadow-lg transition-transform transform hover:scale-95" 
      to={`/Product/${id}`}
    >
      <div className="w-full h-48 flex items-center justify-center overflow-hidden bg-gray-50">
        <img 
          className="max-h-full max-w-full object-contain" 
          src={Array.isArray(image) ? image[0] : image} 
          alt={name || "Product"} 
        />
      </div>
      <p className="pt-3 pb-1 text-sm font-medium text-center">{name}</p>
      <p className="text-sm font-medium text-center">
        {currency}{price}
      </p>
    </Link>
  );
};

export default Productitem;
