import React from "react";
import { useSearchParams } from "react-router-dom";   // ðŸ‘ˆ must import this
import { products } from "./products"; // adjust path if needed

const ProductDetails = () => {
  const [searchParams] = useSearchParams();
  const id = searchParams.get("id");  // ðŸ‘ˆ get ?id=1 from URL

  const product = products.find((p) => p._id.toString() === id);

  if (!product) {
    return <p className="text-center text-red-500">Product not found</p>;
  }

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold">{product._name}</h1>
      <img
        src={product._image[0]}
        alt={product._name}
        className="w-64 h-64 object-cover my-4"
      />
      <p className="text-lg font-semibold">${product._price}</p>
    </div>
  );
};

export default ProductDetails;
