import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const Shopbyproducts = ({ id, Subtitle, title, image, name }) => {
  const [isSquare, setIsSquare] = useState(false);

  useEffect(() => {
    if (image && image[0]) {
      const img = new Image();
      img.src = image[0];
      img.onload = () => {
        setIsSquare(img.width === img.height); // ✅ detect square images
      };
    }
  }, [image]);

  return (
    <Link
      className="block text-gray-700 cursor-pointer hover:shadow-xl transition-transform duration-300 rounded-lg overflow-hidden"
      to={`/product?id=${id}`}
    >
      {/* Card container */}
      <div className="p-3 flex flex-col items-center">
        {/* ✅ Title */}
        {title && (
          <p className="pt-2 pb-1 text-lg text-center font-semibold">{title}</p>
        )}

        {/* ✅ Image wrapper with centering */}
        <div
          className={`relative w-full aspect-square flex items-center justify-center bg-white`}
        >
          <img
            className={`max-h-full max-w-full ${
              isSquare ? "object-contain" : "object-cover"
            }`}
            src={image && image[0] ? image[0] : "/assets/placeholder.png"}
            alt={name || "Product"}
          />
        </div>

        {/* ✅ Product Info */}
        <p className="pt-3 pb-1 text-base font-bold text-center">{name}</p>
        {Subtitle && (
          <p className="text-sm text-center font-medium text-gray-600">
            {Subtitle}
          </p>
        )}
      </div>
    </Link>
  );
};

export default Shopbyproducts;
